mapper.update:
update activity
set is_deleted = 1
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into activity (id, gmt_create, gmt_modified, 
  activity_des, activity_img, activity_info_img, 
  activity_content, activity_info_content, is_deleted
  )
values (#{id,jdbcType=INTEGER}, #{gmtCreate,jdbcType=BIGINT}, #{gmtModified,jdbcType=BIGINT}, 
  #{activityDes,jdbcType=VARCHAR}, #{activityImg,jdbcType=VARCHAR}, #{activityInfoImg,jdbcType=VARCHAR}, 
  #{activityContent,jdbcType=VARCHAR}, #{activityInfoContent,jdbcType=VARCHAR}, #{isDeleted,jdbcType=TINYINT}
  )
