mapper.select:
select goods_type_id
from gtype_vs_gid
where goods_id = #{id}

select

from goods


		goods_name like '%${goodsName}%'


		and store_id = #{storeId}


		and gmt_create >
		#{gmtCreate}



	limit #{start} , #{pageSize}

select count(*) from goods


		goods_name like '%${goodsName}%'


		and store_id = #{storeId}


		and gmt_create >
		#{gmtCreate}
